import React, { useState } from 'react';
import './General.css';
import List from '../List/List';

function General() {
    const [lists, setLists] = useState([]);

    const addList = () => {
        setLists([...lists, { id: Date.now() }]);
    };

    const deleteLists = () => {
        setLists([]);
    };

    const saveLists = () => {
        // Code pour sauvegarder les listes ouvertes
        console.log('Listes sauvegardées:', lists);
    };

    return (
        <div>
            <button className="buttonAdd" onClick={addList}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>

            {lists.map((list) => (
                <List key={list.id} />
            ))}

            <button className="deleteButton" onClick={deleteLists}>
                    Supprimer les listes
            </button>
            <button className="saveButton" onClick={saveLists}>
                    Sauvegarder les listes
            </button>

        </div>
    );
}

export default General;
