import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';
import '../animations.css';
import Movie from './ToDoMovie.mp4';

function Welcome() {
    const navigate = useNavigate();

    const handleClick = () => {
        document.querySelector('.title').classList.add('slide-out-left');
        document.querySelector('.video-background')
        setTimeout(() => {
            navigate('/General');
        }, 500);
    };

    return (
        <div className="video-background">
            <video autoPlay loop muted className="video">
                <source src={Movie} type="video/mp4" />
            </video>
            <div className="title">
                <h1>Welcome to ToDoList.App !</h1>
                <button className="start" onClick={handleClick}>
                    <span className="circle" aria-hidden="true">
                        <span className="icon arrow"></span>
                    </span>
                    <span className="button-text">Start a List</span>
                </button>
            </div>
        </div>
    );
}

export default Welcome;
