import React, { useState } from 'react';
import './List.css';

function List() {
  const [modals, setModals] = useState([]);

  const openModal = () => {
    setModals([...modals, true]);
  };

  const closeModal = (index) => {
    const updatedModals = [...modals];
    updatedModals[index] = false;
    setModals(updatedModals);
  };

  return (
    <div>
      <button className="buttonAdd" onClick={openModal}>
        <span className="icon">
          <i className="fa-solid fa-plus" alt="Add Icon"></i>
        </span>
      </button>
      {modals.map((modalVisible, index) => (
        modalVisible && (
          <div className="modalBackground" key={index}>
            <div className="modalContent">
              <h2>List #{index + 1}</h2>
                <form>
                <div className="inputContainer">
                    <div className="inputWrapper">
                    <input type="text" placeholder="Votre texte ici" />
                    </div>
                </div>
                </form>
              <button className="modalCloseButton" onClick={() => closeModal(index)}>
                {/* Ic√¥ne de fermeture ici */}
              </button>
            </div>
          </div>
        )
      ))}
    </div>
  );
}

export default List;
