import React, { useState } from 'react';
import './General.css';
import List from '../List/List'; 

function General() {
    const [lists, setLists] = useState([]);

    const addList = () => {
        setLists([...lists, <List key={lists.length} />]);
    };

    return (
        <div>
            <button className="buttonAdd" onClick={addList}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>


            {lists.map((list, index) => (
                <div key={index}>
                    {list}
                </div>
            ))}
        </div>
    );
}

export default General;
