import React, { useState, useEffect, useRef } from 'react';
import './List.css';

function List() {
  const [modals, setModals] = useState([]);
  const modalRef = useRef();

  const openModal = () => {
    setModals([...modals, true]);
  };

  const closeModal = (index) => {
    const updatedModals = [...modals];
    updatedModals[index] = false;
    setModals(updatedModals);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closeModal(modals.length - 1);
      }
    };

    window.addEventListener('mousedown', handleClickOutside);
    return () => {
      window.removeEventListener('mousedown', handleClickOutside);
    };
  }, [modals]);

  return (
    <main onClick={openModal}>
      <div>
        <button className="buttonAdd" onClick={(e) => {e.stopPropagation(); openModal();}}>
          <span className="icon">
            <i className="fa-solid fa-plus" alt="Add Icon"></i>
          </span>
        </button>
        {modals.map((modalVisible, index) => (
          modalVisible && (
            <div className="modalBackground" key={index} ref={modalRef}>
              <div className="modalContent">
                <h2>List #{index + 1}</h2>
              </div>
            </div>
          )
        ))}
      </div>
    </main>
  );
}

export default List;
