import React, { useState } from 'react';
import './List.css';

function List() {
  const [modals, setModals] = useState([]); // Tableau d'états pour les modales
  const [inputValue, setInputValue] = useState(""); // État pour stocker la valeur du champ de texte

  const openModal = () => {
    setModals([...modals, true]); // Ouvre une nouvelle modale
  };

  const closeModal = (index) => {
    const updatedModals = [...modals];
    updatedModals[index] = false; // Ferme la modale à l'index donné
    setModals(updatedModals);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value); // Met à jour la valeur du champ de texte
  };

  const handleSubmit = (event, index) => {
    event.preventDefault();
    // Faites quelque chose avec la valeur du champ de texte ici
    console.log("Valeur du champ de texte :", inputValue);
    closeModal(index); // Ferme la modale après la soumission du formulaire
  };

  return (
    <div>
      <button className="buttonAdd" onClick={openModal}>
        <span className="icon">
          <i className="fa-solid fa-plus" alt="Add Icon"></i>
        </span>
      </button>
      {modals.map((modalVisible, index) => (
        modalVisible && (
          <div className="modalBackground" key={index}>
            <div className="modalContent">
              {/* Contenu de votre modal */}
              <h2>List #{index + 1}</h2>
              <form onSubmit={(event) => handleSubmit(event, index)}>
                <input
                  type="text"
                  value={inputValue}
                  onChange={handleInputChange}
                  placeholder="Tapez votre texte ici"
                />
                <button className="buttonValid" type="submit">Valider</button>
              </form>
              <button className="modalCloseButton" onClick={() => closeModal(index)}>
                {/* Icône de fermeture ici */}
              </button>
            </div>
          </div>
        )
      ))}
    </div>
  );
}

export default List;
