import React, { useState } from 'react';
import './General.css';
import List from '../List/List.jsx';  // Assurez-vous d'avoir le bon chemin d'importation vers le composant List

function General() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            <button className="buttonAdd" onClick={openModal}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>
            {isModalOpen && <List closeModal={closeModal} />}
        </div>
    );
}

export default General;
