import React, { useEffect, useState } from 'react';

function Archive() {
    const [archiveData, setArchiveData] = useState([]);

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem('listData')) || {};
        const listsData = Object.values(storedData); // Convertir l'objet en tableau de listes
        setArchiveData(listsData);
    }, []);

    const [expandedLists, setExpandedLists] = useState({});

    const toggleListItems = (listId) => {
        setExpandedLists(prevState => ({
            ...prevState,
            [listId]: !prevState[listId]
        }));
    };

    return (
        <div>
            <h1>Archive</h1>
            <ul>
                {archiveData.map((list) => (
                    <li key={list.id}>
                        <strong onClick={() => toggleListItems(list.id)}>
                            List #{list.number}
                        </strong>
                        {expandedLists[list.id] && (
                            <ul>
                                {list.listItems.map((item, index) => (
                                    <li key={index}>{item}</li>
                                ))}
                            </ul>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Archive;
