import React, { useState, useEffect } from 'react';

function Archive() {
    const [archiveData, setArchiveData] = useState([]);

    useEffect(() => {
        // Récupérez les données du localStorage et mettez-les à jour dans l'état local
        const storedData = Object.values(localStorage)
            .filter(item => item.startsWith('listData-'))
            .map(item => JSON.parse(item));
        setArchiveData(storedData);
    }, []);

    return (
        <div>
            <h1>Archived Lists</h1>
            <ul>
                {archiveData.map(item => (
                    <li key={item.id}>
                        <strong>List Number: {item.number}</strong>
                        <ul>
                            {item.listItems.map((task, index) => (
                                <li key={index}>
                                    {task}
                                </li>
                            ))}
                        </ul>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Archive;
