import React, { useState, useEffect } from 'react';
import './General.css';
import List from '../List/List';
import { useNavigate } from 'react-router-dom';
import '../animations.css';

function General() {
    const navigate = useNavigate();
    const [isArchiveClicked, setIsArchiveClicked] = useState(false);
    const [notification, setNotification] = useState(null);

    const handleClick = () => {
        setIsArchiveClicked(true);
        document.querySelector('.buttonAdd').classList.add('slide-out-left');
        document.querySelector('.deleteButton').classList.add('slide-out-left');
        document.querySelector('.saveButton').classList.add('slide-out-left');
        document.querySelector('.archiveButton').classList.add('slide-out-left');
        setTimeout(() => {
            navigate('/Archive');
        }, 500);
    };

    const [lists, setLists] = useState([]);
    const [listNumber, setListNumber] = useState(1);

    // Charger les listes depuis le localStorage lors du chargement initial
    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem('listData')) || [];
        if (storedData.length > 0) {
            setLists(storedData);
        }
    }, []);

    const addList = () => {
        setLists([...lists, { id: Date.now(), number: listNumber }]);
        setListNumber(listNumber + 1);
    };

    const deleteList = (id) => {
        setLists(lists.filter((list) => list.id !== id));
        if (lists.length === 1) {
            setListNumber(1);
        }
    };

    const deleteLists = () => {
        setLists([]);
        setListNumber(1);
    };

    const saveLists = () => {
        // Créez un tableau de listData contenant le titre et les éléments de chaque liste
        const listData = lists.map((list) => {
            const { id, number } = list;
            const listTitle = localStorage.getItem(`listTitle-${id}`) || `List #${number}`;
            const listItems = JSON.parse(localStorage.getItem(`listItems-${id}`)) || [];
            return { id, number, listTitle, listItems };
        });

        // Enregistrez listData dans le localStorage
        localStorage.setItem('listData', JSON.stringify(listData));

        setNotification('Listes sauvegardées avec succès !');
        setTimeout(() => {
            setNotification(null);
        }, 3000);
        console.log('Listes sauvegardées:', listData);
    };


    return (
        <div>
            <button className="buttonAdd buttonContainer" onClick={addList}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>

            {lists.map((list) => (
                <List
                    key={list.id}
                    id={list.id}
                    listNumber={list.number}
                    onClose={deleteList}
                    isArchiveClicked={isArchiveClicked}
                />
            ))}

            <div className="buttonContainer">
                <button className="deleteButton" onClick={deleteLists}>
                    <span className="iconD">
                        <i className="fa-solid fa-trash"></i>
                    </span>
                </button>
                <button className="saveButton" onClick={saveLists}>
                    <span className="iconS">
                        <i className="fa-solid fa-cloud"></i>
                    </span>
                </button>
                <button className="archiveButton" onClick={handleClick}>
                    <span>
                        <i className="fa-solid fa-box-archive"></i>
                    </span>
                </button>
            </div>
            {notification && <div className="notification">{notification}</div>}
        </div>
    );
}

export default General;
