import React, { useState } from 'react';
import './General.css';
import List from './Liste'; // Assure-toi d'importer le composant Liste

function General() {
    const [showList, setShowList] = useState(false);

    return (
        <div>
            <button className="buttonAdd" onClick={() => setShowList(!showList)}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>

            {showList && <List />}
        </div>
    );
}

export default General;
