import React, { useState } from 'react';
import './List.css';

function List() {
  const [modals, setModals] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const openModal = () => {
    setModals([...modals, true]);
  };

  const closeModal = (index) => {
    const updatedModals = [...modals];
    updatedModals[index] = false;
    setModals(updatedModals);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = (event, index) => {
    event.preventDefault();
    console.log("Valeur du champ de texte :", inputValue);
    closeModal(index);
  };

  return (
    <div>
      <button className="buttonAdd" onClick={openModal}>
        <span className="icon">
          <i className="fa-solid fa-plus" alt="Add Icon"></i>
        </span>
      </button>
      {modals.map((modalVisible, index) => (
        modalVisible && (
          <div className="modalBackground" key={index}>
            <div className="modalContent">
              <h2>List #{index + 1}</h2>
              <form onSubmit={(event) => handleSubmit(event, index)}>
                <div className="inputContainer">
                  <button className="buttonValid" type="submit">Valider</button>
                  <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    placeholder="Tapez votre texte ici"
                    className="inputField"
                  />
                </div>
              </form>
              <button className="modalCloseButton" onClick={() => closeModal(index)}>
                {/* Ic√¥ne de fermeture ici */}
              </button>
            </div>
          </div>
        )
      ))}
    </div>
  );
}

export default List;
