import React, { useState, useEffect } from "react";
import './Archive.css';

function Archive() {
    const [archivedLists, setArchivedLists] = useState([]);

    useEffect(() => {
        // Récupérer les listes archivées depuis le localStorage lors du chargement initial
        const savedLists = JSON.parse(localStorage.getItem('listItems')) || [];
        setArchivedLists(savedLists);
    }, []);

    const handleClick = (list) => {
        // Lorsque l'utilisateur clique sur le titre, afficher les éléments de la liste
        const listItemsString = list.items && list.items.length > 0
            ? list.items.map(item => `- ${item}`).join('\n')
            : 'Aucun élément dans la liste.';
        alert(`List Title: ${list.title}\nList Items:\n${listItemsString}`);
    };


    return (
        <div className="container">
            <h1 className="archives">Archived Lists</h1>
            <ul className="archivedLists">
                {archivedLists.map((list, index) => (
                    <li key={index} onClick={() => handleClick(list)}>
                        {list.title}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Archive;
