import React, { useState, useRef, useEffect } from 'react';
import './List.css';

function List(props) {
    const [position, setPosition] = useState({ x: 0, y: 0 });
    const [size, setSize] = useState({ width: 300, height: 200 }); // Taille initiale
    const listRef = useRef(null);
    const [isDragging, setIsDragging] = useState(false);
    const [isResizing, setIsResizing] = useState(false);
    const [offset, setOffset] = useState({ x: 0, y: 0 });

    useEffect(() => {
        // Calculer la position initiale au milieu de la page
        const windowWidth = window.innerWidth;
        const windowHeight = window.innerHeight;
        const listWidth = size.width;
        const listHeight = size.height;

        const initialX = (windowWidth - listWidth) / 2;
        const initialY = (windowHeight - listHeight) / 2;

        setPosition({ x: initialX, y: initialY });
    }, [size]);

    const handleMouseDown = (e) => {
        if (isResizing) {
            setIsDragging(false);
            setOffset({
                x: e.clientX - size.width,
                y: e.clientY - size.height
            });
        } else {
            setIsDragging(true);
            setIsResizing(false);
            setOffset({
                x: e.clientX - position.x,
                y: e.clientY - position.y
            });
        }
    };

    const handleMouseMove = (e) => {
        if (isDragging) {
            setPosition({
                x: e.clientX - offset.x,
                y: e.clientY - offset.y
            });
        } else if (isResizing) {
            setSize({
                width: e.clientX - offset.x,
                height: e.clientY - offset.y
            });
        }
    };

    const handleMouseUp = () => {
        setIsDragging(false);
        setIsResizing(false);
    };

    const handleResizeMouseDown = (e) => {
        setIsResizing(true);
        setOffset({
            x: e.clientX - size.width,
            y: e.clientY - size.height
        });
    };

    return (
        <div
            ref={listRef}
            className="list-container"
            style={{
                left: `${position.x}px`,
                top: `${position.y}px`,
                width: `${size.width}px`,
                height: `${size.height}px`,
                position: 'absolute'
            }}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        >
            <div
                className="resize-handle"
                onMouseDown={handleResizeMouseDown}
            />
            {/* Contenu de la liste */}
        </div>
    );
}

export default List;
