import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';
import '../animations.css';
import { useTheme } from './ThemeContext'; // Importez le hook useTheme depuis votre fichier ThemeContext

function Welcome() {
    const { isDarkMode } = useTheme(); // Utilisez le hook useTheme ici
    const navigate = useNavigate();

    const handleClick = () => {
        document.querySelector('.title').classList.add('slide-out-left');
        setTimeout(() => {
            navigate('/General');
        }, 500); 
    };

    return (
        <div className={`title ${isDarkMode ? 'dark-mode' : 'light-mode'}`}>
            <h1>Welcome to ToDoList.App !</h1>
            <button className="start" onClick={handleClick}>
                <span className="circle" aria-hidden="true">
                    <span className="icon arrow"></span>
                </span>
                <span className="button-text">Start a List</span>
            </button>
        </div>
    );
}

export default Welcome;
