import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './General.css';
import List from '../List/List';
import { useNavigate } from 'react-router-dom';
import '../animations.css';

function General() {
    const navigate = useNavigate();
    const [isArchiveClicked, setIsArchiveClicked] = useState(false);
    const [notification, setNotification] = useState(null);
    const [lists, setLists] = useState([]);
    const [listNumber, setListNumber] = useState(1);

    // Charger les listes depuis le serveur lors du chargement initial
    useEffect(() => {
        axios.get('/api/lists')
            .then(response => {
                setLists(response.data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);

    const addList = () => {
        const newList = { id: Date.now(), number: listNumber, items: [] };
        axios.post('/api/lists', newList)
            .then(response => {
                setLists([...lists, response.data]);
                setListNumber(listNumber + 1);
            })
            .catch(error => {
                console.error('Error adding list:', error);
            });
    };

    const deleteList = (id) => {
        axios.delete(`/api/lists/${id}`)
            .then(() => {
                setLists(lists.filter((list) => list.id !== id));
                if (lists.length === 1) {
                    setListNumber(1);
                }
            })
            .catch(error => {
                console.error('Error deleting list:', error);
            });
    };

    const deleteLists = () => {
        axios.delete('/api/lists')
            .then(() => {
                setLists([]);
                setListNumber(1);
            })
            .catch(error => {
                console.error('Error deleting lists:', error);
            });
    };

    const saveLists = () => {
        setNotification('Listes sauvegardées avec succès !');
        setTimeout(() => {
            setNotification(null);
        }, 3000);
        console.log('Listes sauvegardées:', lists);
    };

    const handleClick = () => {
        setIsArchiveClicked(true);
        // ... autres actions à effectuer lors du clic sur le bouton d'archivage ...
    };


    return (
        <div>
            <button className="buttonAdd buttonContainer" onClick={addList}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>

            {lists.map((list) => (
                <List
                    key={list.id}
                    id={list.id}
                    listNumber={list.number}
                    onClose={deleteList}
                    isArchiveClicked={isArchiveClicked}
                />
            ))}

            <div className="buttonContainer">
                <button className="deleteButton" onClick={deleteLists}>
                    <span className="iconD">
                        <i className="fa-solid fa-trash"></i>
                    </span>
                </button>
                <button className="saveButton" onClick={saveLists}>
                    <span className="iconS">
                        <i className="fa-solid fa-cloud"></i>
                    </span>
                </button>
                <button className="archiveButton" onClick={handleClick}>
                    <span>
                        <i className="fa-solid fa-box-archive"></i>
                    </span>
                </button>
            </div>
            {notification && <div className="notification">{notification}</div>}
        </div>
    );
}

export default General;
