import React, { useState, useEffect } from 'react';
import './General.css';
import List from '../List/List';
import { useNavigate } from 'react-router-dom';
import '../animations.css';

function General() {
    const navigate = useNavigate();
    const [isArchiveClicked, setIsArchiveClicked] = useState(false);
    const [notification, setNotification] = useState(null);
    const [lists, setLists] = useState([]);
    const [listNumber, setListNumber] = useState(1);

    const handleClick = () => {
        setIsArchiveClicked(true);
        document.querySelector('.buttonAdd').classList.add('slide-out-left');
        document.querySelector('.deleteButton').classList.add('slide-out-left');
        document.querySelector('.archiveButton').classList.add('slide-out-left');
        setTimeout(() => {
            navigate('/Archive');
        }, 500);
    };

    useEffect(() => {
        const storedData = JSON.parse(localStorage.getItem('lists')) || [];
        if (storedData.length > 0) {
            setLists(storedData);
        }
    }, []);

    const addList = () => {
        const newList = { id: Date.now(), number: listNumber, listItems: [] };
        setLists(prevLists => [...prevLists, newList]);
        setListNumber(listNumber + 1);
        // Mettez à jour la liste complète dans le localStorage
        localStorage.setItem('lists', JSON.stringify([...lists, newList]));
    };

    const archiveList = (id) => {
        // Trouvez la liste à archiver
        const archivedList = lists.find(list => list.id === id);
        // Créez une nouvelle liste de listes sans la liste à archiver
        const updatedLists = lists.filter(list => list.id !== id);
        // Mettez à jour la liste complète dans le localStorage sans la liste archivée
        localStorage.setItem('lists', JSON.stringify(updatedLists));
        // Faites tout traitement nécessaire avec la liste archivée
        // ...

        setLists(updatedLists);
    };

    return (
        <div>
            <button className="buttonAdd buttonContainer" onClick={addList}>
                <span className="icon">
                    <i className="fa-solid fa-plus" alt="Add Icon"></i>
                </span>
            </button>

            {lists.map((list) => (
                <List
                    key={list.id}
                    id={list.id}
                    listNumber={list.number}
                    onClose={archiveList}
                    isArchiveClicked={isArchiveClicked}
                />
            ))}

            <div className="buttonContainer">
                <button className="archiveButton" onClick={() => setIsArchiveClicked(true)} onClick={handleClick}>
                    <span>
                        <i className="fa-solid fa-box-archive"></i>
                    </span>
                </button>
            </div>
            {notification && <div className="notification">{notification}</div>}
        </div>
    );
}

export default General;
